INSERT INTO plans_v2 (id, provider, name, name_en, category, country, base_price, currency, external_link, brochure_link, benefits, benefits_en, min_age, max_age, tags) VALUES ('plan_7d8e60c414a0bf45', 'Colmena Seguros', 'Seguro de Vida con Devolución de Prima Banco Caja Social (Plan D)', 'Life Insurance with Premium Refund Banco Caja Social (Plan D)', 'vida', 'CO', 600000, 'COP', 'https://www.colmenaseguros.com/soluciones-para-personas/protege-tu-vida/seguro-de-vida-con-devolucion-de-prima-banco-caja-social', NULL, '["Muerte por cualquier causa: Protección en caso de tu fallecimiento por cualquier motivo a excepción de muerte por guerra civil y/o internacional, tus beneficiarios recibirán la suma estipulada en tu póliza.","Sobrevivencia: Si gozas de la vida al finalizar la vigencia de tu “Seguro de Vida con Devolución de Prima Banco Caja Social”, recibirás el 50% del valor de la prima de tu plan.","Enfermedades graves (Cáncer): Al ser diagnosticado por primera vez, durante la vigencia de tu seguro, te entregaremos un porcentaje de la suma asegurada que corresponda a tu póliza. Se cubren los siguientes tipos de cáncer: Cáncer Invasivo, Cáncer Metastásico, Cáncer de Mama, Próstata y Cérvix, Cáncer de piel, Cáncer In Situ.","Incapacidad total temporal: En caso de que tengas una enfermedad o accidente que resulte en una incapacidad total temporal, recibirás un monto asegurado proporcional al tiempo que tengas en tu incapacidad médica."]', '["Death for any cause: Protection in case of your death for any reason (except civil and/or international war), your beneficiaries will receive the agreed-upon sum.","Survival: If you are alive at the end of the policy term of your \"Life Insurance with Premium Refund Banco Caja Social\", you will receive 50% of the premium value of your plan.","Critical Illnesses (Cancer): If diagnosed for the first time during the validity of your insurance, we will deliver a percentage of the insured sum corresponding to your policy. The following types of cancer are covered: Invasive Cancer, Metastatic Cancer, Breast, Prostate and Cervical Cancer, Skin Cancer, In Situ Cancer.","Temporary Total Disability: In case of an illness or accident resulting in temporary total disability, you will receive an insured amount proportional to the time you are on medical leave."]', NULL, NULL, '["internacional"]');
